import React from "react";
import { View, Text, StyleSheet, Dimensions } from "react-native";

export default function DietTab({progress, meals} : any) {

    const vw = Dimensions.get('window').width;
    const vh = Dimensions.get('window').height;

    const style = StyleSheet.create({
        container: {
            width: 0.8*vw,
            height: 0.4*vh,
            backgroundColor: '#152238',
            borderRadius: 10
        },
        progress_bar: {
            width: progress/100*0.8*vw,
            height: 0.05*vh,
            backgroundColor: '#4461ae',
            borderRadius: 10,
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'flex-end'
        },
        progress_wrapper: {
            width: 0.8*vw,
            height: 0.05*vh,
            backgroundColor: '#152238',
            borderRadius: 10
        },
        table_frow: {
            width: 0.8*vw,
            height: 0.07*vh,
            backgroundColor: '#1c1c1c',
            display: 'flex',
            flexDirection: 'row',
            alignItems: 'center',
            justifyContent: 'space-evenly'
        },
        table_row: {
            width: 0.8*vw,
            height: 0.05*vh,
            backgroundColor: '#0d0e0e'
        }
    })
    return (
        <View style={style.container}>
            <View style={style.progress_wrapper}>
                <View style={style.progress_bar}><Text>{progress}%</Text></View>
            </View>
            <View>
                <View style={style.table_frow}>
                    <Text>Type</Text>
                    <Text>Amount</Text>
                    <Text>Calories</Text>
                </View>
                <View>
                    <Text>Type</Text>
                    <Text>Amount</Text>
                    <Text>Calories</Text>
                </View>
            </View>
        </View>
    )
}
