import React from "react";
import { StyleSheet } from "react-native";

export const style = StyleSheet.create({
    content: {
        width: '60vw',
        height: '50vh',
        right: '1rem',
        top: '3rem',
        backgroundColor: 'black',
        display: 'flex',
        flexDirection: 'column',
        gap: '0',
        border: '2px solid gray',
        position: "absolute",
        transform: "animate all 1s",
        borderRadius: "10px"
    },
    option: {
        width: '100%',
        height: '2rem',
        margin: 0,
        border: '2px 0px 2px 0px solid gray',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center'
    },
    textColor: {
        color: 'white'
    }

})
